import { createUserWithEmailAndPassword, GoogleAuthProvider, sendEmailVerification, sendPasswordResetEmail, signInWithEmailAndPassword, signInWithPopup, updatePassword } from "firebase/auth";
import {auth} from "./firebase";
import { redirect } from "react-router-dom";
import { Children } from "react";
import useAuth from ".";

export const doCreateUserWithEmailAndPassword = async (email,password) => {
    return createUserWithEmailAndPassword(auth,email,password);  
};

export const doSignInWithEmailAndPassword = async (email,password) => {
    return signInWithEmailAndPassword(auth,email,password);  
};

export const doSignInWithGoogle = async () => {
    const provider = new GoogleAuthProvider();
    const result = await signInWithPopup(auth,provider);
    //result.user
    return result;
};

export const doSignOut = () => {
    return auth.signOut();
};


export const Auth = ({Children,redirect} = "/") =>{
 const user = useAuth()
    if (user === null) {
        // หากยังไม่ล็อกอิน (user === null) ให้ redirect ไปยังหน้า login หรือ register
        return <Navigate to={redirect} />;
      }
    
      // หากผู้ใช้ล็อกอินแล้ว ให้แสดงคอมโพเนนต์ที่ได้รับเป็น children
      return <>{children}</>;
    };

