import React from 'react';
import { Navigate } from 'react-router-dom';
import useAuth from './index'; // นำเข้า useAuth hook เพื่อตรวจสอบสถานะผู้ใช้

const PrivateRouteLogout = ({ children, redirectToo }) => {
  const { user } = useAuth();

  // ถ้าผู้ใช้ไม่ได้เข้าสู่ระบบ, เปลี่ยนเส้นทางไปยัง redirectToo
  if (user == null) {
    return <Navigate to={redirectToo} />;
  }

 
  return children;
};

export default PrivateRouteLogout;
