// src/PrivateRoute.js
import React from 'react';
import { Navigate } from 'react-router-dom';
import useAuth from './index'; // นำเข้า useAuth hook เพื่อตรวจสอบสถานะผู้ใช้

const PrivateRoute = ({ children,redirectTo }) => {
  const {user} = useAuth(); // ใช้ custom hook เพื่อเช็คสถานะผู้ใช้

  // ถ้ายังไม่ล็อกอิน, redirect ไปยังหน้า login
  if (user) {
    return <Navigate to={redirectTo || "/home"} />;
  }if(!user){}

  // ถ้าล็อกอินแล้ว, แสดงหน้า (children) ที่ต้องการเข้าถึง
  return children;
};

export default PrivateRoute;
