import React, { useEffect, useState } from 'react';
import { Navigate } from 'react-router-dom';
import useAuth from './index'; // นำเข้า useAuth hook เพื่อตรวจสอบสถานะผู้ใช้

const PrivateRouteLogin = ({ children, redirectTo }) => {
  const { user } = useAuth(); // ใช้ custom hook เพื่อเช็คสถานะผู้ใช้
  const [authenticated, setAuthenticated] = useState(null); // กำหนดค่าเริ่มต้นเป็น null เพื่อแสดงสถานะโหลด

  useEffect(() => {
    // ตรวจสอบสถานะการเข้าสู่ระบบเมื่อ component ถูก mount
    if (user !== null) {
      setAuthenticated(!!user);
    }
  }, [user]);

  if (authenticated === null) {
    return <div>Loading...</div>; // หรือแสดง spinner / loading indicator
  }

  return authenticated ? (
    children
  ) : (
    <Navigate to={redirectTo} />
  );
};

export default PrivateRouteLogin;
